// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Content {
    struct ContentItem {
        uint id;
        address payable creator;
        string ipfsHash; // Content stored on IPFS
        uint price;
        bool isVerified; // To combat misinformation
        bool isActive;
    }

    uint public contentCount = 0;
    mapping(uint => ContentItem) public contents;

    event ContentCreated(uint id, address creator, string ipfsHash, uint price);
    event ContentVerified(uint id, address verifier);
    event ContentDeactivated(uint id);

    modifier onlyCreator(uint _id) {
        require(contents[_id].creator == msg.sender, "Not the content creator");
        _;
    }

    function uploadContent(string memory _ipfsHash, uint _price) external {
        contentCount++;
        contents[contentCount] = ContentItem(contentCount, payable(msg.sender), _ipfsHash, _price, false, true);
        emit ContentCreated(contentCount, msg.sender, _ipfsHash, _price);
    }

    function verifyContent(uint _id) external {
        // Add access control later
        contents[_id].isVerified = true;
        emit ContentVerified(_id, msg.sender);
    }

    function deactivateContent(uint _id) external onlyCreator(_id) {
        contents[_id].isActive = false;
        emit ContentDeactivated(_id);
    }

    function getContent(uint _id) external view returns (address, string memory, uint, bool, bool) {
        ContentItem memory c = contents[_id];
        return (c.creator, c.ipfsHash, c.price, c.isVerified, c.isActive);
    }
}
