// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Payment {
    struct Transaction {
        uint id;
        address payer;
        address payable creator;
        uint amount;
        uint contentId;
    }

    uint public transactionCount = 0;
    mapping(uint => Transaction) public transactions;

    event PaymentMade(uint id, address payer, address creator, uint contentId, uint amount);

    function payCreator(uint _contentId, address payable _creator, uint _amount) external payable {
        require(msg.value == _amount, "Incorrect amount sent");
        require(msg.value > 0, "Payment must be more than zero");
        
        transactionCount++;
        transactions[transactionCount] = Transaction(transactionCount, msg.sender, _creator, msg.value, _contentId);
        _creator.transfer(msg.value);
        emit PaymentMade(transactionCount, msg.sender, _creator, _contentId, msg.value);
    }

    function getTransaction(uint _id) external view returns (address, address, uint, uint) {
        Transaction memory t = transactions[_id];
        return (t.payer, t.creator, t.contentId, t.amount);
    }
}
